// Check if AI extraction succeeded
if ($json.error) {
	return {
		json: {
			error_occurred: true,
			error_type: "AI_EXTRACTION_FAILED",
			error_message: $json.error.message || "AI extraction failed",
			timestamp: new Date().toISOString()
		}
	};
}

let data;
try {
	data = typeof $json.output === 'string' ? JSON.parse($json.output) : $json.output;
} catch (e) {
	return {
		json: {
			error_occurred: true,
			error_type: "JSON_PARSE_ERROR",
			error_message: e.message,
			timestamp: new Date().toISOString()
		}
	};
}

// Validation Logic
const required = ['vendor_name', 'total_amount', 'invoice_date'];
const missing = required.filter(field => !data[field]);

if (missing.length > 0) {
	return {
		json: {
			error_occurred: true,
			error_type: "MISSING_FIELDS",
			error_message: `Missing: ${missing.join(', ')}`,
			extracted_data: data,
			timestamp: new Date().toISOString()
		}
	};
}

// Amount Validation
if (isNaN(parseFloat(data.total_amount))) {
	return {
		json: {
			error_occurred: true,
			error_type: "INVALID_AMOUNT",
			error_message: "Total Amount is not a number",
			extracted_data: data,
			timestamp: new Date().toISOString()
		}
	};
}

return {
	json: {
		error_occurred: false,
		validated_data: data
	}
};

const data = $json.validated_data;

// Business Logic for GL Coding
let glCategory = "Uncategorized";
// Convert vendor to lowercase for easier matching
const vendor = (data.vendor_name || "").toLowerCase();

// FIX 1: Added "amazon" to the check so it catches "Amazon Web Services"
if (vendor.includes("aws") || vendor.includes("amazon") || vendor.includes("google cloud") || vendor.includes("digitalocean")) {
	glCategory = "6000 - Software & Hosting";
} else if (vendor.includes("uber") || vendor.includes("lyft") || vendor.includes("airline") || vendor.includes("flight")) {
	glCategory = "5000 - Travel & Meals";
} else if (vendor.includes("wework") || vendor.includes("office")) {
	glCategory = "4000 - Rent & Utilities";
}

// Approval Thresholds
let approvalStatus = "Auto-Approved";
// Ensure amount is a number
const amount = parseFloat(data.total_amount);

// FIX 2: Check the HIGHEST value first.
// If we checked > 1000 first, 5200 would trigger that and stop.
if (amount > 5000) {
	approvalStatus = "VP Approval Needed";
} else if (amount > 1000) {
	approvalStatus = "Manager Approval Needed";
}

return {
	json: {
		...data,
		gl_category: glCategory,
		approval_status: approvalStatus,
		case_id: `INV-${Date.now()}`,
		processed_at: new Date().toISOString()
	}
};
