const eventTypes = $input.first().json.collection || [];
const demoEventType = eventTypes.find(e => e.name.toLowerCase().includes('demo'));

if (!demoEventType) {
  return [{
    json: {
      error: 'No demo event type found',
      fallbackUrl: 'https://calendly.com/your-team/demo'
    }
  }];
}

const startTime = new Date();
const endTime = new Date();
endTime.setDate(endTime.getDate() + 7);

return [{
  json: {
    eventTypeUri: demoEventType.uri,
    startTime: startTime.toISOString(),
    endTime: endTime.toISOString(),
    schedulingUrl: demoEventType.scheduling_url
  }
}];

const availableTimes = $input.first().json.collection || [];
const fallbackUrl = $('Prepare Availability Params').first().json.schedulingUrl || 'https://calendly.com/your-team/demo';

if (availableTimes.length === 0) {
  return [{
    json: {
      bookingUrl: fallbackUrl,
      nextSlots: ['No slots in next 7 days', 'Check calendar'],
      slotCount: 0
    }
  }];
}

const formattedSlots = availableTimes.slice(0, 2).map(slot => {
  const date = new Date(slot.start_time);
  return date.toLocaleString('en-US', {
    weekday: 'short',
    month: 'short',
    day: 'numeric',
    hour: 'numeric',
    minute: '2-digit',
    timeZoneName: 'short'
  });
});

const firstSlotUrl = availableTimes[0].scheduling_url || fallbackUrl;

return [{
  json: {
    bookingUrl: firstSlotUrl,
    nextSlots: formattedSlots,
    slotCount: availableTimes.length,
    allSlots: availableTimes.slice(0, 5).map(s => ({
      time: s.start_time,
      url: s.scheduling_url
    }))
  }
}];

