const timestamp = Date.now();
const randomNum = Math.floor(Math.random() * 1000);
const leadId = `LEAD-${timestamp}-${randomNum}`;

// Get fields array
const fields = $input.item.json.body.data.fields;

// Helper to get a field by label or key
function getField(label) {
  return fields.find(f => f.label.includes(label) || f.key === label);
}

// Extract basic info
const name = getField("What's your Name")?.value || "";
const email = getField("email address")?.value || "";

// Business type (multiple choice – get text)
const businessField = getField("What type of business");
let businessType = "";
if (businessField?.value?.length) {
  const selectedId = businessField.value[0];
  const option = businessField.options.find(o => o.id === selectedId);
  businessType = option?.text || "";
}

// Interest level (multiple choice – get text)
const interestField = getField("How interested");
let interestLevel = "";
if (interestField?.value?.length) {
  const selectedId = interestField.value[0];
  const option = interestField.options.find(o => o.id === selectedId);
  interestLevel = option?.text || "";
}

// Services selected (checkboxes – use IDs → text)
const servicesField = getField("What kind of services");
let services = [];
if (servicesField?.value?.length) {
  services = servicesField.value.map(id => {
    const opt = servicesField.options.find(o => o.id === id);
    return opt?.text;
  }).filter(Boolean);
}

// Return final unified structured lead object
return [{
  json: {
    leadId: leadId,
    name,
    email,
    businessType,
    interestLevel,
    services
  }
}];

const data = $input.item.json;

const serviceTagMap = {
  "Podcast Production": "Podcast Lead",
  "Social Media Management": "Social Content Lead",
  "Video Editing": "Video Editing Lead",
  "Website Design": "Website Lead",
  "Marketing Strategy": "Strategy Lead",
  "Content Creation": "Content Lead"
};

const detectedTags = [];

// Tag from selected services
if (data.services) {
  const selectedServices = Array.isArray(data.services) 
    ? data.services 
    : data.services.split(',').map(s => s.trim());

  selectedServices.forEach(service => {
    if (serviceTagMap[service]) {
      detectedTags.push(serviceTagMap[service]);
    }
  });
}

// Keyword detection fallback
const tagKeywords = {
  "Podcast Lead": ["podcast", "audio", "episode"],
  "Social Content Lead": ["social media", "instagram", "facebook", "tiktok"],
  "Video Editing Lead": ["video", "editing", "youtube", "reel"],
  "Website Lead": ["website", "web design", "site", "landing page"],
  "Strategy Lead": ["strategy", "consulting", "growth plan"]
};

const textToAnalyze = [
  data.businessType || "",
  data.services || ""
].join(" ").toLowerCase();

for (const [tag, keywords] of Object.entries(tagKeywords)) {
  for (const keyword of keywords) {
    if (textToAnalyze.includes(keyword) && !detectedTags.includes(tag)) {
      detectedTags.push(tag);
      break;
    }
  }
}

if (detectedTags.length === 0) {
  detectedTags.push("General Lead");
}

const tagsString = detectedTags.join(", ");

return {
  json: {
    ...data,
    tags: tagsString,
    detectedTags: detectedTags
  }
};

const data = $input.item.json;
const today = new Date().toISOString().split('T')[0];

// Calculate next touch based on interest level
let daysToAdd = 7;
if (data.interestLevel.includes("Very Interested")) {
  daysToAdd = 1;
} else if (data.interestLevel.includes("Interested")) {
  daysToAdd = 3;
} else if (data.interestLevel.includes("Somewhat")) {
  daysToAdd = 7;
} else {
  daysToAdd = 14;
}

const nextTouch = new Date();
nextTouch.setDate(nextTouch.getDate() + daysToAdd);
const nextTouchFormatted = nextTouch.toISOString().split('T')[0];

// Initial lead score
let initialScore = 20; // Base score for signing up
if (data.interestLevel.includes("Very Interested")) initialScore = 40;
else if (data.interestLevel.includes("Interested")) initialScore = 30;

return {
  json: {
    ...data,
    createdDate: today,
    lastContact: today,
    nextTouch: nextTouchFormatted,
    status: "New Lead",
    nurtureStage: 0,
    leadScore: initialScore,
    emailOpens: 0,
    emailClicks: 0,
    leadMagnetSent: "NO",
    calendlyClicked: "NO",
    meetingBooked: "NO",
    daysInactive: 0,
    notes: `New lead from form. Tags: ${data.tags}`
  }
};

const data = $input.item.json;
const tags = data.detectedTags || [];

let servicesMention = "";
if (tags.includes("Podcast Lead")) {
  servicesMention = "I noticed you're interested in podcast production. We help creators launch and grow shows that actually get heard.";
} else if (tags.includes("Video Editing Lead")) {
  servicesMention = "I see you're looking for video editing support. We transform raw footage into scroll-stopping content that drives results.";
} else if (tags.includes("Social Content Lead")) {
  servicesMention = "Social media can feel overwhelming, we get it. We help businesses turn social platforms into lead-generating machines.";
} else if (tags.includes("Website Lead")) {
  servicesMention = "Your website should work as hard as you do. We build sites that convert visitors into customers.";
} else if (tags.includes("Strategy Lead")) {
  servicesMention = "Great marketing starts with clear strategy. We help businesses cut through the noise and focus on what actually moves the needle.";
} else {
  servicesMention = "We specialize in digital marketing that gets real results, from content creation to complete campaign management.";
}

const subject = `Welcome, ${data['Name']}! Let's talk about your marketing goals`;

const emailBody = `Hi ${data['Name']},

Thanks for reaching out, I'm glad you found us.

I want to confirm that we received your information and let you know what happens next.

${servicesMention}

Here's what you can expect:

→ We will review your details and put together some initial thoughts
→ You'll hear back from us within 24-48 hours
→ We'll schedule a quick call to discuss your specific needs (no pressure, no sales pitch, just a genuine conversation about how we might help)

In the meantime, feel free to reply to this email with any questions or additional context about what you're trying to achieve. The more I know, the more helpful I can be when we connect.

Looking forward to speaking with you soon.

Best regards,
Gilbert Onyebuchi

P.S. Keep an eye on your inbox. Over the next week, I'll send you some helpful insights about building marketing systems that actually work.`;

return {
  json: {
    ...data,
    emailSubject: subject,
    emailBody: emailBody
  }
};
