/**
 * n8n ‚Äì Code node
 * Mode : Run Once for All Items
 * Objectif :
 *   1. Rep√®re **gras** ou *gras*
 *   2. Convertit le contenu en lettres "Unicode Bold"
 *   3. Supprime tous les ast√©risques
 */

const FIELD = 'output'; // ‚Üê mets ici le nom exact du champ texte

// --- petite fonction de conversion ASCII ‚Üí Unicode gras
function toUnicodeBold(str) {
  const upperOffset = 0x1D400 - 0x41;      // A ‚Üí ùêÄ
  const lowerOffset = 0x1D41A - 0x61;      // a ‚Üí ùêö
  const digitOffset = 0x1D7CE - 0x30;      // 0 ‚Üí ùüé

  return str.split('').map(ch => {
    const code = ch.charCodeAt(0);
    if (code >= 0x41 && code <= 0x5A) return String.fromCodePoint(code + upperOffset); // A-Z
    if (code >= 0x61 && code <= 0x7A) return String.fromCodePoint(code + lowerOffset); // a-z
    if (code >= 0x30 && code <= 0x39) return String.fromCodePoint(code + digitOffset); // 0-9
    return ch; // ponctuation, espaces‚Ä¶
  }).join('');
}

for (const item of $input.all()) {
  if (item.json[FIELD]) {
    item.json[FIELD] = item.json[FIELD]
      // ‚ñ∫ 1. **‚Ä¶**  ou *‚Ä¶*  ‚Üí conversion + suppression d‚Äôast√©risques
      .replace(/\*{1,2}([^*]+?)\*{1,2}/gs, (_, txt) => toUnicodeBold(txt))

      // ‚ñ∫ 2. S‚Äôil reste des ast√©risques isol√©s ‚Üí on les retire
      .replace(/\*/g, '');
  }
}

return $input.all();
