for (const item of $input.all()) {
  let ragOutput = item.json.output || ""; // Assuming the LLM output is in 'output'

  // 1. Sanitize: Remove common conversational boilerplate added by the LLM
  ragOutput = ragOutput.replace(/i am an expert knowledge worker assistant|here are the retrieved facts:|based on my search, the policy states:|please contact support if you need further assistance./gi, '').trim();

  // 2. Add a clear field name for the final result
  // The downstream Resolution Agent tool should be mapped to this field.
  item.json.retrievedFacts = ragOutput;

  // Optional: Remove the messy 'output' field to clean up the data stream
  delete item.json.output; 
}

return $input.all();

for (const item of $input.all()) {
  // 1. Get the raw text output from the AI Agent
  // Adjust 'output' if your agent outputs to a different field name (e.g. 'text')
  let aiResponse = item.json.output || "";

  // 2. CLEANUP: Remove markdown code blocks (```json ... ```) if the AI added them
  // This is a common issue with LLMs even when told not to.
  aiResponse = aiResponse.replace(/```json/gi, "").replace(/```/g, "").trim();

  try {
    // 3. PARSE: Convert the string into a real JSON Object
    const parsedData = JSON.parse(aiResponse);

    // 4. MERGE: Add the clean fields (category, urgency, etc.) to the root JSON
    Object.assign(item.json, parsedData);

    // Optional: Remove the raw messy string to keep things clean
    // delete item.json.output; 

  } catch (error) {
    // 5. ERROR HANDLING: If the AI creates bad JSON, don't crash the workflow.
    // Instead, return a default "General Inquiry" structure so the flow continues.
    item.json.error = "JSON Parsing Failed";
    item.json.category = "General Inquiry"; // Default fallback
    item.json.urgency = "Medium";
    item.json.sentiment = "Neutral";
    item.json.priority_score = 0.5;
    item.json.summary = "Could not parse AI response. defaulting values.";
  }
}

return $input.all();
