parameters.jsCode:
// Extract form data
const body = $input.item.binary;
const params = $input.item.json.body;

// Get quote text
const quote = params.quote || "No quote provided";

// Get image binary data
const imageData = body.image;

if (!imageData) {
  throw new Error("No image uploaded");
}

// Convert binary to base64 for HTML embedding
const base64Image = imageData.data.toString('base64');
const mimeType = imageData.mimeType || 'image/jpeg';

return {
  json: {
    quote: quote,
    imageBase64: base64Image,
    mimeType: mimeType,
    timestamp: new Date().toISOString()
  }
};
