// Get the input items
const documents = $input.all().map(item => item.json);

// Initialize arrays for each category
const recordings = [];
const notes = [];
const chats = [];

// Process each document
documents.forEach(doc => {
  const name = doc.name.toLowerCase();

  // Skip the "RECORDINGS" folder itself
  if (name === 'recordings') {
    return;
  }

  // Categorize based on the document name
  if (name.includes('- recording')) {
    recordings.push(doc);
  } else if (name.includes('- notes by gemini')) {
    notes.push(doc);
  } else if (name.includes('- chat')) {
    chats.push(doc);
  }
});

// Return as separate items for each category
const output = [];

// Add recordings as individual items
recordings.forEach(rec => {
  output.push({
    json: {
      type: 'recording',
      ...rec
    }
  });
});

// Add notes as individual items
notes.forEach(note => {
  output.push({
    json: {
      type: 'note',
      ...note
    }
  });
});

// Add chats as individual items
chats.forEach(chat => {
  output.push({
    json: {
      type: 'chat',
      ...chat
    }
  });
});

return output;

// Extract title and content from Google Docs JSON payload

const items = $input.all();
const results = [];

for (const item of items) {
  // Get the document data - handle both array and object formats
  let docData = item.json;

  // If it's an array (like your payload), get the first element
  if (Array.isArray(docData)) {
    docData = docData[0];
  }

  const title = docData.title || 'Untitled Document';
  const content = docData.body?.content || [];

  // Extract all text content
  let fullText = '';

  for (const element of content) {
    if (element.paragraph) {
      const paragraph = element.paragraph;

      for (const elem of paragraph.elements || []) {
        // Regular text
        if (elem.textRun?.content) {
          fullText += elem.textRun.content;
        }
        // Person mentions
        if (elem.person?.personProperties?.name) {
          fullText += elem.person.personProperties.name;
        }
        // Rich links
        if (elem.richLink?.richLinkProperties?.title) {
          fullText += `[${elem.richLink.richLinkProperties.title}]`;
        }
      }
    }
  }

  // Clean up the text - remove excessive whitespace but preserve structure
  const cleanedContent = fullText
    .replace(/\n{3,}/g, '\n\n')  // Max 2 newlines in a row
    .trim();

  results.push({
    json: {
      title: title,
      content: cleanedContent,
      summary: `## ${title}\n\n${cleanedContent}`
    }
  });
}

return results;
