const videoId = $('Set Variables').item.json.youtubeVideoId;


// n8n HTTP Request (fullResponse + text) usually returns: { statusCode, headers, body }
const raw = ($json && ($json.body ?? $json.data)) ?? null;

let parsed;
try {
  parsed = typeof raw === 'string' ? JSON.parse(raw) : raw;
} catch (e) {
  return [{ json: { videoId, status: 'error', error: 'Invalid JSON response from transcript API', source: 'youtube_transcript_api' } }];
}

// Support multiple possible shapes
const firstItem = Array.isArray(parsed)
  ? parsed[0]
  : (Array.isArray(parsed?.data) ? parsed.data[0] : parsed?.data?.[0]);

// Common fields seen in transcript APIs: text / transcript / tracks[].transcript[]
let text = firstItem?.text ?? firstItem?.transcript ?? null;

if (!text && firstItem?.tracks?.[0]?.transcript?.length) {
  text = firstItem.tracks[0].transcript.map(t => t.text).join(' ');
}

const language = firstItem?.language ?? firstItem?.lang ?? firstItem?.tracks?.[0]?.language

return [{
  json: {
    videoId,
    language,
    text,
    status: text ? 'found' : 'no_transcript',
    source: 'youtube_transcript_api'
  }
}];

const videoId = $json.videoId;
const language = $json.language;


let text = $json.text;

// Normalize to string
text = typeof text === 'string' ? text : JSON.stringify(text);

// Basic cleaning (safe for most transcript sources)
text = text
  .replace(/\r?\n/g, ' ')
  .replace(/\s+/g, ' ')
  .replace(/\[(music|m√∫sica|musica)\]/gi, '')
  .trim();

return [{
  json: {
    videoId,
    language,
    text,
    wordCount: text ? text.split(' ').filter(Boolean).length : 0,
    charCount: text ? text.length : 0,
    status: 'success',
    source: 'youtube_transcript_api'
  }
}];

return [{
  json: {
    videoId: $('Set Variables').item.json.youtubeVideoId,
    language: $('Set Variables').item.json.preferredLanguage || 'es',
    error: 'No transcript available for this video (or API returned empty).',
    status: 'no_transcript',
    suggestion: 'Try another language, or use a Whisper/ASR fallback.',
    source: 'youtube_transcript_api'
  }
}];
