let images = [];
let captions = [];
let hashtags = [];
let schedule = [];

for (const entry of items) {
  const item = entry.json;   // << IMPORTANT FIX

  // Cloudinary images â†’ detect secure_url
  if (item.secure_url) {
    images.push(item.secure_url);
  }

  // Captions
  if (item.output?.captions) {
    captions = item.output.captions;
  }

  // Hashtags
  if (item.output?.hashtags) {
    hashtags = item.output.hashtags;
  }

  // Schedule
  if (item.output?.schedule) {
    schedule = item.output.schedule;
  }
}

return [
  {
    json: {
      images,
      captions,
      hashtags,
      schedule
    }
  }
];
