{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "LinkedInPostsAnalysis",
  "type": "object",
  "additionalProperties": false,
  "required": [
    "latest_post_date",
    "most_recent_posts_summary",
    "posting_frequency",
    "top_topics",
    "recent_highlights",
    "data_quality",
    "notes"
  ],
  "properties": {
    "latest_post_date": {
      "type": "string",
      "format": "date",
      "description": "Exact date of the most recent post, YYYY-MM-DD."
    },
    "most_recent_posts_summary": {
      "type": "string",
      "minLength": 1,
      "description": "2‚Äì4 sentence overview of the latest posts."
    },
    "posting_frequency": {
      "type": "object",
      "additionalProperties": false,
      "required": [
        "average_days_between_posts",
        "cadence_label",
        "posts_per_month_estimate"
      ],
      "properties": {
        "average_days_between_posts": {
          "type": "number",
          "minimum": 0
        },
        "cadence_label": {
          "type": "string",
          "enum": [
            "~multiple per week",
            "~weekly",
            "~biweekly",
            "~monthly",
            "~every 1‚Äì2 months",
            "~sporadic"
          ]
        },
        "posts_per_month_estimate": {
          "type": "number",
          "minimum": 0
        }
      }
    },
    "top_topics": {
      "type": "array",
      "minItems": 3,
      "maxItems": 3,
      "items": {
        "type": "object",
        "additionalProperties": false,
        "required": ["topic", "keywords", "rationale"],
        "properties": {
          "topic": {
            "type": "string",
            "minLength": 1
          },
          "keywords": {
            "type": "array",
            "minItems": 2,
            "maxItems": 6,
            "items": {
              "type": "string",
              "minLength": 1
            }
          },
          "rationale": {
            "type": "string",
            "minLength": 1
          }
        }
      }
    },
    "recent_highlights": {
      "type": "array",
      "minItems": 2,
      "maxItems": 5,
      "items": {
        "type": "object",
        "additionalProperties": false,
        "required": ["date", "summary", "why_it_matters"],
        "properties": {
          "date": {
            "type": "string",
            "format": "date"
          },
          "summary": {
            "type": "string",
            "minLength": 1
          },
          "why_it_matters": {
            "type": "string",
            "minLength": 1
          }
        }
      }
    },
    "data_quality": {
      "type": "object",
      "additionalProperties": false,
      "required": ["num_posts_detected", "num_dates_used", "date_range"],
      "properties": {
        "num_posts_detected": {
          "type": "integer",
          "minimum": 0
        },
        "num_dates_used": {
          "type": "integer",
          "minimum": 0
        },
        "date_range": {
          "type": "object",
          "additionalProperties": false,
          "required": ["start", "end"],
          "properties": {
            "start": {
              "type": "string",
              "format": "date"
            },
            "end": {
              "type": "string",
              "format": "date"
            }
          }
        }
      }
    },
    "notes": {
      "type": "string",
      "description": "Assumptions, ambiguities, or caveats; empty string if none."
    }
  }
}
