// Get all items from previous node
const items = $input.all();

// Get the prompt
let prompt = items[0].json.thumbnailPrompt;

// Check if prompt exists
if (!prompt) {
  throw new Error('thumbnailPrompt is empty or undefined');
}

// Remove leading and trailing quotes if present
if (prompt.startsWith('"') && prompt.endsWith('"')) {
  prompt = prompt.slice(1, -1);
}

// Clean up the prompt
prompt = prompt
  .replace(/\n/g, ' ')
  .replace(/\r/g, ' ')
  .replace(/\s+/g, ' ')
  .replace(/\\"/g, '"')
  .trim();

// Get image URLs from Airtable
const airtableData = $('Get a record2').first().json;

// Try both possible field names
const image1 = airtableData["Reference image 1"]?.[0]?.url || airtableData["Thumbnail reference 1"]?.[0]?.url;
const image2 = airtableData["Reference image 2"]?.[0]?.url || airtableData["Thumbnail reference 2"]?.[0]?.url;

// Debug: Log what we found
console.log('Airtable fields:', Object.keys(airtableData));
console.log('Image1:', image1);
console.log('Image2:', image2);

// Check if images were found
if (!image1 || !image2) {
  throw new Error(`Missing images. Image1: ${!!image1}, Image2: ${!!image2}. Available fields: ${Object.keys(airtableData).join(', ')}`);
}

// Build the JSON body with only 2 images
const bodyRaw = JSON.stringify({
  model: "nano-banana-pro",
  callBackUrl: $execution.resumeUrl,
  input: {
    prompt: prompt,
    image_input: [image1, image2],
    aspect_ratio: "16:9",
    resolution: "1K",
    output_format: "png"
  }
});

// Return the stringified JSON
return [{
  json: {
    bodyRaw: bodyRaw
  }
}];

// Get the transcript array from Apify
const transcriptData = $json.data;

// Check if it's an array
if (!Array.isArray(transcriptData)) {
  throw new Error("Transcript data is not an array");
}

// Convert seconds to HH:MM:SS.mmm format
function formatTimestamp(seconds) {
  const totalSeconds = parseFloat(seconds);
  const hrs = Math.floor(totalSeconds / 3600);
  const mins = Math.floor((totalSeconds % 3600) / 60);
  const secs = Math.floor(totalSeconds % 60);
  const ms = Math.floor((totalSeconds % 1) * 1000);

  return `${String(hrs).padStart(2, '0')}:${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}.${String(ms).padStart(3, '0')}`;
}

// Build clean, readable transcript
let cleanTranscript = "";

for (let i = 0; i < transcriptData.length; i++) {
  const item = transcriptData[i];
  if (item.text && item.text.trim()) {
    cleanTranscript += formatTimestamp(item.start) + " - " + item.text.trim() + "\n\n";
  }
}

// Return formatted transcript
return {
  json: {
    Transcript: cleanTranscript.trim()
  }
};

// Get the AI response
const items = $input.all();

// Handle different possible structures
let rawOutput;
if (items[0].json.output) {
  // Anthropic/Claude structure
  rawOutput = items[0].json.output[0].content[0].text;
} else if (items[0].json.text) {
  // Direct text field
  rawOutput = items[0].json.text;
} else if (items[0].json.choices) {
  // OpenAI structure
  rawOutput = items[0].json.choices[0].message.content;
} else {
  // Fallback - try to stringify and parse
  rawOutput = JSON.stringify(items[0].json);
}

// Remove markdown code fences if present
rawOutput = rawOutput
  .replace(/```json\s*/g, '')  // Remove ```json
  .replace(/```\s*/g, '')       // Remove closing ```
  .trim();

// Parse the JSON string
const clips = JSON.parse(rawOutput);

// Return each clip as a separate item for Airtable
return clips.map(clip => ({
  json: clip
}));

// Get the input
const items = $input.all();

// The input is already an array of clips
const clips = items.map(item => item.json);

// Format each clip as readable text
const formattedClips = clips.map(clip => {
  return `CLIP ${clip.clip_number}
‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ
üìç Start: ${clip.start_timestamp}
üìç End: ${clip.end_timestamp}
üìç CTA Start: ${clip.cta_start_timestamp}
üìç CTA End: ${clip.cta_end_timestamp}

üì∫ Title: ${clip.youtube_title}

üìù Caption:
${clip.caption}
`;
}).join('\n\n');

// Create summary
const summary = `üé¨ TOTAL CLIPS IDENTIFIED: ${clips.length}

${formattedClips}`;

// Return just what you need
return [{
  json: {
    clips_formatted: summary
  }
}];

