// Get the AI analysis output from the previous node
const aiOutput = $input.first().json;

// Extract recommendations array
const recommendations = aiOutput.recommendations || [];

// Prepare email report data (single item)
const emailReport = {
  summary: aiOutput.summary || '',
  keyInsights: aiOutput.keyInsights || [],
  recommendations: recommendations,
  generatedDate: new Date().toISOString()
};

// Create output items: one for email report, followed by individual recommendations
const outputItems = [];

// First item: email report data
outputItems.push({
  json: {
    type: 'email_report',
    ...emailReport
  }
});

// Subsequent items: individual recommendations for task creation
recommendations.forEach((recommendation, index) => {
  outputItems.push({
    json: {
      type: 'task',
      taskNumber: index + 1,
      ...recommendation
    }
  });
});

return outputItems;

