// Validate data quality from merged department data
const items = $input.all();

const qualityIssues = [];
let totalFields = 0;
let missingFields = 0;
let anomalies = [];

// Check if we have data from all departments
const expectedDepartments = ['sales', 'marketing', 'finance', 'support'];
const departmentsPresent = new Set();

items.forEach((item, index) => {
  const data = item.json;

  // Identify department from data structure or source
  if (data.department) {
    departmentsPresent.add(data.department.toLowerCase());
  }

  // Check for missing or null fields
  Object.keys(data).forEach(key => {
    totalFields++;
    if (data[key] === null || data[key] === undefined || data[key] === '') {
      missingFields++;
      qualityIssues.push({
        type: 'missing_field',
        item: index,
        field: key,
        severity: 'medium'
      });
    }
  });

  // Check for numeric anomalies (negative values where they shouldn't be)
  Object.keys(data).forEach(key => {
    if (typeof data[key] === 'number') {
      if (data[key] < 0 && (key.includes('revenue') || key.includes('count') || key.includes('total'))) {
        anomalies.push({
          type: 'negative_value',
          item: index,
          field: key,
          value: data[key],
          severity: 'high'
        });
      }
    }
  });
});

// Check for missing departments
expectedDepartments.forEach(dept => {
  if (!departmentsPresent.has(dept)) {
    qualityIssues.push({
      type: 'missing_department',
      department: dept,
      severity: 'high'
    });
  }
});

// Calculate quality score (0-100)
const completenessScore = totalFields > 0 ? ((totalFields - missingFields) / totalFields) * 100 : 0;
const departmentScore = (departmentsPresent.size / expectedDepartments.length) * 100;
const anomalyPenalty = Math.min(anomalies.length * 5, 30); // Max 30 point penalty

const qualityScore = Math.max(0, Math.round((completenessScore * 0.5 + departmentScore * 0.5) - anomalyPenalty));

// Determine overall quality status
let qualityStatus = 'excellent';
if (qualityScore < 50) {
  qualityStatus = 'poor';
} else if (qualityScore < 70) {
  qualityStatus = 'fair';
} else if (qualityScore < 85) {
  qualityStatus = 'good';
}

// Combine all issues
const allIssues = [...qualityIssues, ...anomalies];

// Return validation results
return [{
  json: {
    qualityScore: qualityScore,
    qualityStatus: qualityStatus,
    totalFields: totalFields,
    missingFields: missingFields,
    completenessPercentage: Math.round(completenessScore),
    departmentsPresent: Array.from(departmentsPresent),
    departmentsMissing: expectedDepartments.filter(d => !departmentsPresent.has(d)),
    anomalyCount: anomalies.length,
    issueCount: allIssues.length,
    issues: allIssues,
    highSeverityIssues: allIssues.filter(i => i.severity === 'high').length,
    passesQualityCheck: qualityScore >= 70,
    validatedAt: new Date().toISOString(),
    originalData: items
  }
}];
