// Build URLs (n8n Code node)

const input = items[0]?.json ?? {};

// since: daily | weekly | monthly
const since = String(input.since || 'daily').toLowerCase().trim();

// languages_csv example: "any,JavaScript,TypeScript,Python,Go,Rust,C++,Java"
const languagesCsv = String(input.languages_csv || 'any').trim();

// Normalize "since"
const allowedSince = new Set(['daily', 'weekly', 'monthly']);
const sinceFinal = allowedSince.has(since) ? since : 'daily';

// Parse languages
let langs = languagesCsv
  .split(',')
  .map(s => s.trim())
  .filter(Boolean);

// remove duplicates
langs = [...new Set(langs.map(l => l.toLowerCase()))];

// map normalized language -> GitHub path slug
const langSlugMap = {
  'any': null,
  'javascript': 'javascript',
  'typescript': 'typescript',
  'python': 'python',
  'go': 'go',
  'rust': 'rust',
  'c++': 'c%2B%2B',     // URL encoded
  'cpp': 'c%2B%2B',     // allow cpp too
  'java': 'java',
};

const nowIso = new Date().toISOString();

// week_id like 2025-W50
function getWeekId(d = new Date()) {
  const date = new Date(Date.UTC(d.getFullYear(), d.getMonth(), d.getDate()));
  const dayNum = date.getUTCDay() || 7;
  date.setUTCDate(date.getUTCDate() + 4 - dayNum);
  const yearStart = new Date(Date.UTC(date.getUTCFullYear(), 0, 1));
  const weekNo = Math.ceil((((date - yearStart) / 86400000) + 1) / 7);
  return `${date.getUTCFullYear()}-W${String(weekNo).padStart(2, '0')}`;
}

const weekId = getWeekId(new Date());

// Build URLs
const urls = [];
for (const l of langs) {
  const slug = langSlugMap[l];
  // skip unknown languages safely
  if (slug === undefined) continue;

  const base = slug ? `https://github.com/trending/${slug}` : `https://github.com/trending`;
  const url = `${base}?since=${encodeURIComponent(sinceFinal)}`;

  urls.push({
    url,
    source_url: url,
    page_index: urls.length,
    since: sinceFinal,
    captured_at: nowIso,
    week_id: weekId,
    // keep your sheet config if you want:
    sheet_id: input.sheet_id,
    raw_sheet_name: input.raw_sheet_name || 'trending_raw',
    brief_sheet_name: input.brief_sheet_name || 'weekly_brief',
  });
}

return urls.map(x => ({ json: x }));

const html = $json.data || $json.body || $json || '';
const upstream = $items('Split URLs (loop)')[0]?.json || {};

const articles = [...String(html).matchAll(/<article[\s\S]*?<\/article>/g)];
let rank = 0;

const cleanText = (s) => String(s || '').replace(/<[^>]+>/g,' ').replace(/\s+/g,' ').trim();

function extractTightDescription(block) {
  const preferred = block.match(/<p[^>]*class="[^"]*\bcol-9\b[^"]*"[^>]*>([\s\S]*?)<\/p>/i);
  if (preferred) return cleanText(preferred[1]);

  const afterTitle = block.match(/<h2[\s\S]*?<\/h2>[\s\S]*?<p[^>]*>([\s\S]*?)<\/p>/i);
  if (afterTitle) return cleanText(afterTitle[1]);

  const anyP = block.match(/<p[^>]*>([\s\S]*?)<\/p>/i);
  return anyP ? cleanText(anyP[1]) : '';
}

function extractCountFromAnchor(block, hrefSuffix) {
  const aMatch = block.match(new RegExp(`(<a[^>]*href=\"\\/[^"]+\\/${hrefSuffix}\"[^>]*>[\\s\\S]*?<\\/a>)`, 'i'));
  if (!aMatch) return '';
  const text = cleanText(aMatch[1]);
  const numMatch = text.match(/([0-9][0-9.,]*\s*[kKmM]?)/);
  return numMatch ? numMatch[1].replace(/\s+/g,'') : '';
}

const extracted = [];
for (const m of articles) {
  const block = m[0];

  const h2RepoMatch = block.match(/<h2[^>]*>[\s\S]*?<a[^>]*href=\"\/([^\/\s]+)\/([^\/"]+)\"/i);
  if (!h2RepoMatch) continue;

  const owner = h2RepoMatch[1].trim();
  const repo = h2RepoMatch[2].trim();
  if (!owner || !repo) continue;
  if (owner.toLowerCase() === 'sponsors') continue;

  rank += 1;

  const full_name = `${owner}/${repo}`;
  const repo_url = `https://github.com/${full_name}`;
  const description = extractTightDescription(block);

  const langMatch = block.match(/itemprop=\"programmingLanguage\"[^>]*>([^<]+)/i);
  const language = langMatch ? cleanText(langMatch[1]) : '';

  const stars_total_raw = extractCountFromAnchor(block, 'stargazers');
  const forks_total_raw = extractCountFromAnchor(block, 'forks');

  const starsInPeriodMatch = block.match(/([0-9.,kKmM]+)\s+stars\s+this\s+week/i) || block.match(/([0-9.,kKmM]+)\s+stars\s+today/i) || block.match(/([0-9.,kKmM]+)\s+stars\s+this\s+month/i);

  extracted.push({
    ...upstream,
    rank_on_page: rank,
    owner,
    repo,
    full_name,
    repo_url,
    description,
    language,
    stars_total_raw,
    forks_total_raw,
    stars_in_period_raw: starsInPeriodMatch?.[1] || ''
  });
}

return extracted.map(r => ({ json: r }));

function parseNumber(s) {
  if (!s) return 0;
  const t = String(s).trim().toLowerCase();
  if (t.endsWith('k')) return Math.round(parseFloat(t.slice(0, -1)) * 1000);
  if (t.endsWith('m')) return Math.round(parseFloat(t.slice(0, -1)) * 1000000);
  return parseInt(t.replace(/,/g, ''), 10) || 0;
}

return items.map((item) => {
  const j = item.json || {};

  const upstreamKeywords = (j.keywords || []).map(k => String(k).toLowerCase());
  const langSet = new Set((j.languages || []).map(l => String(l).toLowerCase()));

  const stars_total = parseNumber(j.stars_total_raw);
  const forks_total = parseNumber(j.forks_total_raw);
  const stars_in_period = parseNumber(j.stars_in_period_raw);

  const text = `${j.full_name || ''} ${j.description || ''}`.toLowerCase();
  const matches = upstreamKeywords.filter(k => k && text.includes(k));
  const uniqueMatches = [...new Set(matches)];
  const is_dev_tool = uniqueMatches.length > 0;

  let keyword_bonus = 0;
  if (is_dev_tool) keyword_bonus = Math.min(20, 10 + Math.max(0, (uniqueMatches.length - 1) * 2));
  const language_bonus = (j.language && langSet.has(String(j.language).toLowerCase())) ? 3 : 0;
  const score = (stars_in_period * 2) + keyword_bonus + language_bonus;

  const dedupe_key = `${j.since}|${j.full_name}`;


  return {
    json: {
      captured_at: j.captured_at,
      week_id: j.week_id,
      since: j.since,
      source_url: j.source_url,
      rank_on_page: j.rank_on_page,
      full_name: j.full_name,
      repo_url: j.repo_url,
      owner: j.owner,
      repo: j.repo,
      description: j.description,
      language: j.language,
      stars_total,
      forks_total,
      stars_in_period,
      keyword_matches: uniqueMatches.join(', '),
      is_dev_tool,
      score,
      dedupe_key,
      sheet_id: j.sheet_id,
      raw_sheet_name: j.raw_sheet_name,
      brief_sheet_name: j.brief_sheet_name
    }
  };
});

const weekId = ($items('Build URLs')[0]?.json?.week_id) || '';
let rows = items.map(x => x.json || {});
if (rows.length === 1 && Array.isArray(rows[0].data)) rows = rows[0].data;

const weekRows = rows.filter(r => String(r.week_id || '') === String(weekId));
const devRows = weekRows.filter(r => String(r.is_dev_tool).toLowerCase() === 'true');

const top = [...weekRows].sort((a,b)=> (Number(b.score)||0) - (Number(a.score)||0)).slice(0,10);

const top_repos = top.map(r => r.repo_url).join('\n');
const top_10_list = top.map((r,i)=> `${i+1}) ${r.full_name} — stars_in_period=${r.stars_in_period||0}, lang=${r.language||''}, score=${r.score||0}`).join('\n');

const themeCounts = {};
for (const r of devRows) {
  const parts = String(r.keyword_matches || '').split(',').map(s=>s.trim()).filter(Boolean);
  for (const p of parts) themeCounts[p] = (themeCounts[p] || 0) + 1;
}
const top_themes = Object.entries(themeCounts).sort((a,b)=>b[1]-a[1]).slice(0,20).map(([k,v])=>`${k}(${v})`).join(', ');

const why = top.length
  ? `Top repo this ${$items('Build URLs')[0]?.json?.since || 'period'}: ${top[0].full_name} (${top[0].language || 'n/a'}) with ${top[0].stars_in_period || 0} stars in period.`
  : '';

return [{
  json: {
    week_id: weekId,
    generated_at: new Date().toISOString(),
    total_repos_captured: weekRows.length,
    total_devtools_flagged: devRows.length,
    top_10_list,
    top_repos,
    top_themes,
    notes: why,
    message_sent_to: '',
    link_to_raw_filter: ''
  }
}];
