const keywords = [
  'software vulnerability',
  'cybersecurity',
  'memory safety'
];

return keywords.map(k => ({
  json: {
    url: `https://patents.google.com/?q=${encodeURIComponent(k)}&oq=${encodeURIComponent(k)}&sort=new`,
    keyword: k
  }
}));

const results = [];
$input.all().forEach(item => {
  if (Array.isArray(item.json.patents)) {
    item.json.patents.forEach(patent => results.push({json: patent}));
  }
});
return results;

const items = $input.all();
return items.map(item => {
  const api = item.json;
  const enriched = api.patents && api.patents[0] ? api.patents[0] : {};
  const abstract = item.json.abstract || '';
  const important = /security|vulnerability|overflow/i.test(abstract) || (enriched.cpcs || []).some(c => /g06f/i.test(c.cpc_subgroup_id || ''));
  return {
    json: {
      title: item.json.title,
      applicationNumber: item.json.applicationNumber,
      publicationDate: item.json.publicationDate,
      abstract,
      url: item.json.url,
      cpc: (enriched.cpcs || []).map(c => c.cpc_subgroup_id).join(', '),
      inventors: (enriched.inventors || []).map(i => i.name_first + ' ' + i.name_last),
      important
    }
  };
});
