const databasePages = $input.all();
const scrapedResults = $('Scrape Target URLs').all();

if (databasePages.length !== scrapedResults.length) {
  throw new Error('Database pages count does not match scraped results');
}

let blocks = [];

for (let i = 0; i < databasePages.length; i++) {
  const pageId = databasePages[i].json.id;
  const scrapedContent = scrapedResults[i].json.data.markdown || '';

  // Split into chunks of max 1500 chars
  const chunkSize = 1500;
  const chunks = [];

  for (let start = 0; start < scrapedContent.length; start += chunkSize) {
    chunks.push(scrapedContent.slice(start, start + chunkSize));
  }

  // Build objects for next node (Append block)
  for (const chunk of chunks) {
    blocks.push({
      pageId,
      chunk
    });
  }
}

return [
  {
    json: {
      blocks
    },
    pairedItem: 0
  }
];

const targetURLs = $('Split Out').all();
const currentScrapedResults = $('Scrape Target URLs').all();
const currentScreenshots = $('Capture Screenshots').all();

const lastSnapshots = $('Get Last Snapshots').all();

const fetchedBlocks = $input.all();

let websites = [];

for (let targetURL of targetURLs) {
  targetURL = targetURL.json.target_urls;

  const lastSnapshot = lastSnapshots.find((item) => item.json.property_url.startsWith(targetURL))?.json;

  if(!lastSnapshot) continue;

  const lastSnapshotDate = lastSnapshot.property_created_date.start;

  const lastScreenshot = lastSnapshot.property_screenshot[0];

  const currentScrapedResult = currentScrapedResults.find((item) => item.json.data.metadata.url.startsWith(targetURL));

  const currentScreenshot = currentScreenshots.find((item) => item.json.data.metadata.url.startsWith(targetURL)).json.data.screenshot;

 const currentDate = new Date().toISOString().split('T')[0];

  const currentContent = currentScrapedResult.json.data.markdown;

  const blocks = fetchedBlocks.filter((item) => item.json.parent_id == lastSnapshot.id);

  const lastContent = blocks.map((block) => block.json.content).join('');

  websites.push({
    url: targetURL,
    currentDate,
    currentContent,
    currentScreenshot,
    lastSnapshotDate,
    lastContent,
    lastScreenshot,
  })

}

return [
  {
    json: {
      websites
    },
    pairedItem: 0
  }
]

