const aiAnalyses = $input.all();
const websites = $('Split Out Data').all();

if(aiAnalyses.length != websites.length) {
  throw new Error(`Mismatch in data length: ${aiAnalyses.length} AI analyses but ${websites.length} websites`);
}

let updates = [];

for (let i = 0; i < websites.length; i++) {

  const summary = aiAnalyses[i].json.output[0].content[0].text;

  if (summary.includes("NO_CHANGE")) continue;

  updates.push({
    ...websites[i].json,
    summary
  });
}

return [
  {
    json: {
      updates
    },
    pairedItem: 0
  }
];

