parameters.jsCode:
let article = "";

// Introduction
article += $input.first().json.message.content.introduction;
article += "<br><br>";

// Chapters
const chapters = $input.first().json.message.content.chapters;
for (let i = 0; i < chapters.length; i++) {
  // âœ… Access properties directly (no .json)
  article += "<h2>" + chapters[i].title + "</h2>";
  article += chapters[i].prompt; // âœ… Use 'content' field
  article += "<br><br>";
}

// Conclusions
article += "<h2>Final words</h2>";
article += $input.first().json.message.content.conclusions;

// FAQ Section
article += "<h2>FAQ Section</h2>";
const faqSection = $input.first().json.message.content['FAQ section'];

if (Array.isArray(faqSection)) {
  for (const faq of faqSection) {
    if (faq.question && faq.answer) {
      article += `<h3>${faq.question}</h3>`;
      article += `<p>${faq.answer}</p>`;
    }
  }
} else if (typeof faqSection === 'object' && faqSection !== null) {
  if (faqSection.question && faqSection.answer) {
    article += `<h3>${faqSection.question}</h3>`;
    article += `<p>${faqSection.answer}</p>`;
  }
} else {
  article += `<p>${faqSection}</p>`;
}

return [
  {
    "article": article
  }
];

function wrapText(text, maxCharsPerLine = 30) {
    const words = text.split(' ');
    let lines = [];
    let currentLine = '';

    for (const word of words) {
        if ((currentLine + ' ' + word).trim().length <= maxCharsPerLine) {
            currentLine += ' ' + word;
        } else {
            lines.push(currentLine.trim());
            currentLine = word;
        }
    }
    if (currentLine) lines.push(currentLine.trim());
    return lines.join('\n'); // Add line breaks
}

// Get your title
const title =$('Generate all the necessary information of post').first().json.message.content.title

// Wrap the text
items[0].json.wrappedTitle = wrapText(title, 29); // 25 chars per line, adjust as needed

return items;

